import React, { useState } from 'react';
import axios from 'axios';

const WelcomeScreen = ({ adSoyad, sube: ilkSube, girisBilgisi }) => {
    const [mevcutSube, setMevcutSube] = useState(ilkSube); // **Dinamik şube**
    const [aiOneri, setAiOneri] = useState(null);
    const [aiAciklama, setAiAciklama] = useState('');
    const [enYakinSube, setEnYakinSube] = useState(null);
    const [serbestSecim, setSerbestSecim] = useState('');
    const [mesaj, setMesaj] = useState('');
    const [guncellendi, setGuncellendi] = useState(false);
    const [tumSubeler, setTumSubeler] = useState([]); // Serbest seçim için
    const [secimler, setSecimler] = useState([]);
    const [aktifSecim, setAktifSecim] = useState('ai'); // ai, enYakin, serbest

    const tercihOpsiyonlari = [
        'ATM yoğunluğu az',
        'Engelli erişimi var',
        'Yoğunluk düşük',
        'Park yeri mevcut',
        'Uzun çalışma saatleri',
        'Kolay ulaşım'
    ];

    // Serbest seçim için şube listesi yükle (bir kez)
    React.useEffect(() => {
        axios.get('/subeler.json')
            .then(res => setTumSubeler(res.data.subeler || []))
            .catch(() => setTumSubeler([]));
    }, []);

    const tercihDegistir = (tercih) => {
        if (secimler.includes(tercih)) {
            setSecimler(secimler.filter((s) => s !== tercih));
        } else {
            setSecimler([...secimler, tercih]);
        }
    };

    // Yapay zeka ve en yakın şube aynı anda gelsin!
    const getAiSubeOnerisi = async () => {
        setMesaj('');
        try {
            const response = await axios.post('http://localhost:5000/gemini', {
                il: 'İstanbul',
                secimler,
                konum: mevcutSube
            });
            if (response.data.oneri) {
                setAiOneri(response.data.oneri);
                setAiAciklama(response.data.aciklama || '');
                setEnYakinSube(response.data.enYakin);
            } else {
                setMesaj('Yapay zekâ önerisi alınamadı.');
            }
        } catch (err) {
            setMesaj('Yapay zekâ hizmetine ulaşılamadı.');
        }
    };

    // Şube güncellemesi
    const subeDegistir = async (yeniSube) => {
        try {
            const response = await axios.post('http://localhost:5000/sube-guncelle', {
                girisBilgisi,
                yeniSube: yeniSube
            });
            if (response.data.basarili) {
                setGuncellendi(true);
                setMevcutSube(yeniSube); // Güncel şubeyi anında göster!
                setMesaj(response.data.mesaj);
            } else {
                setMesaj(response.data.mesaj);
            }
        } catch (error) {
            setMesaj('Sunucu hatası oluştu.');
        }
    };

    // Şube önerisi hazır değilse, yükleme tuşu göster
    const aiBtnEnabled = aiOneri && enYakinSube;

    // Seçime göre buton aktifliği ve ismi
    let secilenSube = "";
    if (aktifSecim === "ai") secilenSube = aiOneri;
    if (aktifSecim === "enYakin") secilenSube = enYakinSube;
    if (aktifSecim === "serbest") secilenSube = serbestSecim;

    return (
        <div style={styles.container}>
            <h2>Hoş geldiniz, {adSoyad}!</h2>
            <p>Mevcut şubeniz: <strong>{mevcutSube}</strong></p>

            {!guncellendi ? (
                <>
                    <p><strong>Yapay zekâ destekli şube önerisi almak ister misiniz?</strong></p>
                    {/* Tercih listesi */}
                    <div style={{ marginTop: '10px' }}>
                        <p><strong>Şube tercihlerinizi seçin:</strong></p>
                        {tercihOpsiyonlari.map((tercih) => (
                            <label key={tercih} style={{ display: 'block', margin: '5px 0' }}>
                                <input
                                    type="checkbox"
                                    checked={secimler.includes(tercih)}
                                    onChange={() => tercihDegistir(tercih)}
                                />{" "}
                                {tercih}
                            </label>
                        ))}
                    </div>
                    <button onClick={getAiSubeOnerisi} style={styles.aiBtn}>Yapay Zekâ ile Şube Öner</button>

                    {/* 3 Seçenekli öneri kutusu */}
                    {(aiOneri || enYakinSube) && (
                        <div style={{ marginTop: 20, border: '1px solid #eee', padding: 15, borderRadius: 8, background: "#f8f8f8" }}>
                            <strong>Bir öneri seçin:</strong>
                            <div style={{ display: "flex", flexDirection: "column", gap: 8, marginTop: 8 }}>
                                {/* Yapay Zekâ önerisi */}
                                <label>
                                    <input
                                        type="radio"
                                        checked={aktifSecim === "ai"}
                                        onChange={() => setAktifSecim("ai")}
                                        disabled={!aiOneri}
                                    />{" "}
                                    <strong>Yapay Zekâ En Uygun Şube:</strong> {aiOneri || "-"}
                                    {aiAciklama && <span style={{ fontStyle: "italic", color: "#004fa3" }}> <br />{aiAciklama}</span>}
                                </label>
                                {/* En yakın şube */}
                                <label>
                                    <input
                                        type="radio"
                                        checked={aktifSecim === "enYakin"}
                                        onChange={() => setAktifSecim("enYakin")}
                                        disabled={!enYakinSube}
                                    />{" "}
                                    <strong>En Yakın Şube (Konum Bazlı):</strong> {enYakinSube || "-"}
                                </label>
                                {/* Serbest seçim */}
                                <label>
                                    <input
                                        type="radio"
                                        checked={aktifSecim === "serbest"}
                                        onChange={() => setAktifSecim("serbest")}
                                    />{" "}
                                    <strong>Serbest Seçim:</strong>
                                    <select
                                        value={serbestSecim}
                                        onChange={e => setSerbestSecim(e.target.value)}
                                        style={{ marginLeft: 8 }}
                                    >
                                        <option value="">Bir şube seçin...</option>
                                        {tumSubeler.map(s => (
                                            <option key={s.isim} value={s.isim}>{s.isim}</option>
                                        ))}
                                    </select>
                                </label>
                            </div>
                            <button
                                onClick={() => subeDegistir(secilenSube)}
                                style={styles.button}
                                disabled={!secilenSube}
                            >
                                Bu şubeye geç
                            </button>
                        </div>
                    )}

                    {mesaj && <p style={{ color: 'red' }}>{mesaj}</p>}
                </>
            ) : (
                <p style={{ color: 'green', marginTop: '20px' }}>{mesaj}<br />Yeni şubeniz: <b>{mevcutSube}</b></p>
            )}
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '60px',
        padding: '20px',
        maxWidth: '600px',
        margin: 'auto',
        backgroundColor: '#fff',
        border: '1px solid #ccc',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)'
    },
    button: {
        padding: '10px 20px',
        marginTop: '15px',
        backgroundColor: '#be1e2d',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    },
    aiBtn: {
        padding: '10px 20px',
        marginTop: '15px',
        backgroundColor: '#0066cc',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    }
};

export default WelcomeScreen;
