// frontend/src/components/TercihSiralama.js
import React from "react";
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';

const grid = 8;

const getItemStyle = (isDragging, draggableStyle) => ({
    userSelect: "none",
    padding: 14,
    margin: `0 0 ${grid}px 0`,
    borderRadius: 7,
    background: isDragging ? "#fbeaec" : "#fff",
    border: isDragging ? "1.5px solid #be1e2d" : "1.2px solid #eee",
    color: "#1a1a1a",
    fontWeight: 600,
    boxShadow: isDragging ? "0 2px 12px #be1e2d22" : "0 1px 4px #bbb1",
    ...draggableStyle
});

const getListStyle = isDraggingOver => ({
    background: isDraggingOver ? "#fff4f5" : "#f7f7f7",
    padding: grid,
    borderRadius: 10,
    minHeight: 180
});

export default function TercihSiralama({
    tercihler,
    onSiralamaOnayla,
    onIptal
}) {
    const [items, setItems] = React.useState(tercihler);

    React.useEffect(() => {
        setItems(tercihler);
    }, [tercihler]);

    function onDragEnd(result) {
        if (!result.destination) return;
        const yeniItems = Array.from(items);
        const [removed] = yeniItems.splice(result.source.index, 1);
        yeniItems.splice(result.destination.index, 0, removed);
        setItems(yeniItems);
    }

    return (
        <div>
            <h3 style={{ marginBottom: 8 }}>Tercihlerinizi Öncelik Sırasına Göre Sıralayın</h3>
            <DragDropContext onDragEnd={onDragEnd}>
                <Droppable droppableId="tercih-listesi">
                    {(provided, snapshot) => (
                        <div
                            ref={provided.innerRef}
                            style={getListStyle(snapshot.isDraggingOver)}
                            {...provided.droppableProps}
                        >
                            {items.map((tercih, index) => (
                                <Draggable key={tercih} draggableId={tercih} index={index}>
                                    {(provided, snapshot) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            style={getItemStyle(
                                                snapshot.isDragging,
                                                provided.draggableProps.style
                                            )}
                                        >
                                            {index + 1}. {tercih}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
            <div style={{ display: "flex", justifyContent: "center", gap: 12, marginTop: 22 }}>
                <button
                    onClick={() => onSiralamaOnayla(items)}
                    style={{
                        padding: "10px 24px",
                        background: "#be1e2d",
                        color: "#fff",
                        border: "none",
                        borderRadius: 6,
                        fontWeight: 600,
                        fontSize: 15,
                        cursor: "pointer"
                    }}
                >
                    Sıralamayı Onayla ve Yapay Zekâdan Öneri Al
                </button>
                <button
                    onClick={onIptal}
                    style={{
                        padding: "10px 16px",
                        background: "#e2e2e2",
                        color: "#1a1a1a",
                        border: "none",
                        borderRadius: 6,
                        fontWeight: 500,
                        fontSize: 15,
                        cursor: "pointer"
                    }}
                >
                    Tercihleri Değiştir
                </button>
            </div>
        </div>
    );
}
