import React, { useState } from 'react';
import axios from 'axios';

const WelcomeScreen = ({ adSoyad, sube }) => {
    const [yeniSube] = useState('Beşiktaş Şubesi'); // önerilen şube
    const [guncellendi, setGuncellendi] = useState(false);
    const [mesaj, setMesaj] = useState('');

    const girisBilgisi = adSoyad.split(' ')[0].toLowerCase(); // örnek amaçlı, kullanıcı adı gibi kullanıyoruz

    const subeDegistir = async () => {
        try {
            const response = await axios.post('http://localhost:5000/sube-guncelle', {
                girisBilgisi,
                yeniSube
            });

            if (response.data.basarili) {
                setGuncellendi(true);
                setMesaj(response.data.mesaj);
            } else {
                setMesaj(response.data.mesaj);
            }
        } catch (error) {
            setMesaj('Sunucu hatası oluştu.');
        }
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '80px' }}>
            <h2>Hoş geldiniz, {adSoyad}!</h2>
            <p>Mevcut şubeniz: <strong>{sube}</strong></p>

            {!guncellendi ? (
                <>
                    <p>Önerilen yeni şube: <strong>{yeniSube}</strong></p>
                    <button onClick={subeDegistir} style={styles.button}>Bu şubeye geç</button>
                </>
            ) : (
                <p style={{ color: 'green', marginTop: '20px' }}>{mesaj}</p>
            )}
        </div>
    );
};

const styles = {
    button: {
        padding: '10px 20px',
        marginTop: '10px',
        backgroundColor: '#be1e2d',
        color: '#fff',
        border: 'none',
        cursor: 'pointer'
    }
};

export default WelcomeScreen;
