// src/components/LoginForm.js

import React, { useState } from 'react';
import axios from 'axios';
import WelcomeScreen from './WelcomeScreen';

const LoginForm = () => {
    const [girisBilgisi, setGirisBilgisi] = useState('');
    const [kullanici, setKullanici] = useState(null);
    const [hata, setHata] = useState('');

    const handleLogin = async () => {
        if (!girisBilgisi) {
            setHata('Lütfen TC Kimlik No veya Kullanıcı Adınızı girin.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5000/giris', {
                girisBilgisi: girisBilgisi
            });

            if (response.data.basarili) {
                setKullanici(response.data);
                setHata('');
            } else {
                setHata('Kullanıcı bulunamadı.');
                setKullanici(null);
            }
        } catch (err) {
            setHata('Sunucuya bağlanılamadı.');
        }
    };

    if (kullanici) {
        return <WelcomeScreen adSoyad={kullanici.adSoyad} sube={kullanici.sube} />;
    }

    return (
        <div style={styles.container}>
            <img src="logoo.jpg" alt="Ziraat Logo" style={styles.logo} />
            <h1 style={styles.title}>Ziraat Bankası</h1>
            <input
                type="text"
                placeholder="TC Kimlik No veya Kullanıcı Adı"
                value={girisBilgisi}
                onChange={(e) => setGirisBilgisi(e.target.value)}
                style={styles.input}
            />
            <button onClick={handleLogin} style={styles.button}>
                Giriş Yap
            </button>
            {hata && <p style={styles.error}>{hata}</p>}
        </div>
    );
};

const styles = {
    container: {
        width: '100%',
        maxWidth: '400px',
        margin: '80px auto',
        textAlign: 'center',
        padding: '20px',
        border: '1px solid #ccc',
        borderRadius: '12px',
        boxShadow: '0px 0px 8px rgba(0,0,0,0.2)',
        backgroundColor: '#f9f9f9'
    },
    logo: {
        width: '120px',
        marginBottom: '10px'
    },
    title: {
        fontSize: '24px',
        marginBottom: '20px',
        color: '#be1e2d'
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px'
    },
    button: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#be1e2d',
        color: '#fff',
        border: 'none',
        cursor: 'pointer'
    },
    error: {
        color: 'red',
        marginTop: '10px'
    }
};

export default LoginForm;
