import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TercihSiralama from "./TercihSiralama";

// Spinner bileşeni
const Spinner = () => (
    <span style={{
        display: 'inline-block',
        verticalAlign: 'middle',
        marginLeft: 8,
        width: 22,
        height: 22
    }}>
        <svg viewBox="0 0 50 50" style={{ width: 22, height: 22 }}>
            <circle cx="25" cy="25" r="20" fill="none" stroke="#be1e2d" strokeWidth="5" strokeLinecap="round"
                strokeDasharray="31.415, 31.415"
                transform="rotate(-90 25 25)">
                <animateTransform attributeName="transform" type="rotate" from="0 25 25"
                    to="360 25 25" dur="1s" repeatCount="indefinite" />
            </circle>
        </svg>
    </span>
);

const WelcomeScreen = ({ adSoyad, sube: ilkSube, girisBilgisi }) => {
    const [mevcutSube, setMevcutSube] = useState(ilkSube);
    const [aiOneri, setAiOneri] = useState(null);
    const [aiAciklama, setAiAciklama] = useState('');
    const [enYakinSube, setEnYakinSube] = useState(null);
    const [serbestSecim, setSerbestSecim] = useState('');
    const [mesaj, setMesaj] = useState('');
    const [guncellendi, setGuncellendi] = useState(false);
    const [tumSubeler, setTumSubeler] = useState([]);
    const [secimler, setSecimler] = useState([]);
    const [aktifSecim, setAktifSecim] = useState('ai');
    const [eskiSube, setEskiSube] = useState(ilkSube);
    const [aiYukleniyor, setAiYukleniyor] = useState(false);

    const [siralaModu, setSiralaModu] = useState(false);
    const [siralananTercihler, setSiralananTercihler] = useState([]);

    // Uyarı mesajı state'i
    const [siralaUyarisi, setSiralaUyarisi] = useState('');

    const tercihOpsiyonlari = [
        'ATM Yoğunluğu Az',
        'Engelli Erişimi Var',
        'Yoğunluk Düşük',
        'Park Yeri Mevcut',
        'Uzun Çalışma Saatleri',
        'Kolay Ulaşım',
        'Bireysel Bankacılık Hizmeti',
        'Kurumsal Bankacılık Hizmeti',
        'Kobi Bankacılığı Hizmeti'
    ];

    useEffect(() => {
        axios.get('http://localhost:5000/subeler')
            .then(res => setTumSubeler(res.data.subeler || []))
            .catch(() => setTumSubeler([]));
    }, []);

    const tercihDegistir = (tercih) => {
        if (secimler.includes(tercih)) {
            setSecimler(secimler.filter((s) => s !== tercih));
            setMesaj('');
        } else {
            const yeniSecimler = [...secimler, tercih];
            setSecimler(yeniSecimler);
            if (yeniSecimler.length === 4) {
                setMesaj('En fazla 4 tercih yapabilirsiniz.');
            } else {
                setMesaj('');
            }
        }
    };

    // YAPAY ZEKA ŞUBE ÖNER butonuna basıldığında
    const handleOnerClick = () => {
        setSiralaUyarisi('');
        if (secimler.length > 1 && siralananTercihler.length < 2) {
            setSiralaUyarisi('Birden fazla tercih varsa önce tercihler sıralanmalı.');
            return;
        }
        getAiSubeOnerisi(secimler.length > 1 ? siralananTercihler : secimler);
    };

    const getAiSubeOnerisi = async (tercihlerSirali) => {
        setMesaj('');
        setAiYukleniyor(true);
        try {
            const response = await axios.post('http://localhost:5000/gemini', {
                il: 'İstanbul',
                secimler: tercihlerSirali,
                konum: mevcutSube
            });
            if (response.data.oneri) {
                setAiOneri(response.data.oneri);
                setAiAciklama(response.data.aciklama || '');
                setEnYakinSube(response.data.enYakin);
            } else {
                setMesaj('Yapay zekâ önerisi alınamadı.');
            }
        } catch (err) {
            setMesaj('Yapay zekâ hizmetine ulaşılamadı.');
        } finally {
            setAiYukleniyor(false);
        }
    };



    const subeDegistir = async (yeniSube) => {
        try {
            const response = await axios.post('http://localhost:5000/sube-guncelle', {
                girisBilgisi,
                yeniSube: yeniSube
            });
            if (response.data.basarili) {
                setGuncellendi(true);
                setEskiSube(mevcutSube);
                setMevcutSube(yeniSube);
                setMesaj(response.data.mesaj);
            } else {
                setMesaj(response.data.mesaj);
            }
        } catch (error) {
            setMesaj('Sunucu hatası oluştu.');
        }
    };

    let secilenSube = "";
    if (aktifSecim === "ai") secilenSube = aiOneri;
    if (aktifSecim === "enYakin") secilenSube = enYakinSube;
    if (aktifSecim === "serbest") secilenSube = serbestSecim;

    return (
        <div style={styles.wrapper}>
            <div style={styles.card}>

                {/* YENİ: Kart içeriğini saran kapsayıcı */}
                <div
                    style={
                        aiYukleniyor
                            ? { pointerEvents: "none", opacity: 0.56, filter: "blur(0.5px)" }
                            : {}
                    }
                >
                    {siralaModu && (
                        <TercihSiralama
                            tercihler={secimler}
                            onSiralamaOnayla={tercihlerSirali => {
                                setSiralananTercihler(tercihlerSirali);
                                setSiralaModu(false);
                            }}
                            onIptal={() => setSiralaModu(false)}
                        />
                    )}



                    {!guncellendi ? (
                        <>
                            <div style={styles.header}>
                                <img src="/logoo.jpg" alt="Ziraat Logo" style={styles.logo} />
                                <h1 style={styles.title}>
                                    Ziraat Bankası<br />Yapay Zeka Destekli Müşteri Şube Devri
                                </h1>
                            </div>
                            <p style={{ fontSize: 16, marginBottom: 10 }}>
                                Hoş geldiniz, <strong style={{ color: '#be1e2d' }}>{adSoyad}</strong>!
                            </p>
                            <p style={styles.label}>
                                Mevcut şubeniz: <strong style={{ color: '#be1e2d' }}>{mevcutSube}</strong>
                            </p>
                            <div style={styles.box}>
                                <p><b>Yapay zekâ destekli şube önerisi almak ister misiniz?</b></p>
                                <div>
                                    <p style={{ marginBottom: 4 }}>Şube tercihlerinizi seçin:</p>
                                    <div style={styles.tercihlerVertical}>
                                        {tercihOpsiyonlari.map((tercih) => (
                                            <label key={tercih} style={{
                                                ...styles.checkboxLabel,
                                                fontWeight: secimler.includes(tercih) ? 'bold' : 'normal',
                                                color: secimler.includes(tercih) ? '#be1e2d' : '#333'
                                            }}>
                                                <input
                                                    type="checkbox"
                                                    checked={secimler.includes(tercih)}
                                                    onChange={() => tercihDegistir(tercih)}
                                                    disabled={!secimler.includes(tercih) && secimler.length >= 4}
                                                /> {tercih}
                                            </label>
                                        ))}
                                    </div>
                                    {mesaj && (
                                        <p style={{ fontSize: 13.5, color: '#be1e2d', marginTop: 4 }}>{mesaj}</p>
                                    )}
                                </div>
                                {secimler.length > 1 && (
                                    <div style={{ display: "flex", justifyContent: "center", marginTop: 14 }}>
                                        <button
                                            style={{
                                                padding: "10px 24px",
                                                background: "#be1e2d",
                                                color: "#fff",
                                                border: "none",
                                                borderRadius: 6,
                                                fontWeight: 600,
                                                fontSize: 16,
                                                cursor: "pointer"
                                            }}
                                            onClick={() => setSiralaModu(true)}
                                        >
                                            Tercihleri Sırala
                                        </button>
                                    </div>
                                )}

                                {secimler.length === 1 && (
                                    <div style={{ display: "flex", justifyContent: "center", marginTop: 16 }}>
                                        <button
                                            style={styles.aiBtn}
                                            disabled={aiYukleniyor}
                                            onClick={() => getAiSubeOnerisi(secimler)}
                                        >
                                            {aiYukleniyor ? <>Bekleyiniz... <Spinner /></> : "Yapay Zeka ile Şube Öner"}
                                        </button>
                                    </div>
                                )}
                                {secimler.length > 1 && (
                                    <div style={{ display: "flex", justifyContent: "center", marginTop: 16, flexDirection: "column", alignItems: "center" }}>
                                        <button
                                            style={styles.aiBtn}
                                            disabled={aiYukleniyor}
                                            onClick={handleOnerClick}
                                        >
                                            {aiYukleniyor ? <>Bekleyiniz... <Spinner /></> : "Yapay Zeka ile Şube Öner"}
                                        </button>
                                        {siralaUyarisi && (
                                            <p style={{ fontSize: 13, color: "#be1e2d", marginTop: 6 }}>{siralaUyarisi}</p>
                                        )}

                                    </div>
                                )}
                            </div>
                            {(aiOneri || enYakinSube) && (
                                <div style={styles.oneriBox}>
                                    <strong>Bir öneri seçin:</strong>
                                    <div style={styles.radioGroup}>
                                        {/* Yapay Zeka Anket Sonucuna Uygun Şubeye Ata */}
                                        <label
                                            style={{
                                                ...styles.radioLabel,
                                                ...(aktifSecim === "ai" ? styles.radioSelected : {})
                                            }}
                                        >
                                            <input
                                                type="radio"
                                                checked={aktifSecim === "ai"}
                                                onChange={() => setAktifSecim("ai")}
                                                disabled={!aiOneri}
                                            />
                                            <span style={{ fontWeight: 600 }}>
                                                Yapay Zeka Anket Sonucuna Uygun Şubeye Ata:
                                            </span>{" "}
                                            {/* Açıklama veya diğer içeriği sadece aktifse etkin yap */}
                                            <span
                                                style={{
                                                    opacity: aktifSecim === "ai" ? 1 : 0.5,
                                                    pointerEvents: aktifSecim === "ai" ? "auto" : "none"
                                                }}
                                            >
                                                {aiOneri || "-"}
                                                {aiAciklama && (
                                                    <span style={{ fontStyle: "italic", color: "#4e4e4e" }}>
                                                        <br />
                                                        {aiAciklama}
                                                    </span>
                                                )}
                                            </span>
                                        </label>
                                        {/* En Kısa Mesafedeki Şubeye Ata */}
                                        <label
                                            style={{
                                                ...styles.radioLabel,
                                                ...(aktifSecim === "enYakin" ? styles.radioSelected : {})
                                            }}
                                        >
                                            <input
                                                type="radio"
                                                checked={aktifSecim === "enYakin"}
                                                onChange={() => setAktifSecim("enYakin")}
                                                disabled={!enYakinSube}
                                            />
                                            <span style={{ fontWeight: 600 }}>
                                                En Kısa Mesafedeki Şubeye Ata:
                                            </span>{" "}
                                            <span
                                                style={{
                                                    opacity: aktifSecim === "enYakin" ? 1 : 0.5,
                                                    pointerEvents: aktifSecim === "enYakin" ? "auto" : "none"
                                                }}
                                            >
                                                {enYakinSube || "-"}
                                            </span>
                                        </label>
                                        {/* Serbest Seçim Şubesine Ata */}
                                        <label
                                            style={{
                                                ...styles.radioLabel,
                                                ...(aktifSecim === "serbest" ? styles.radioSelected : {})
                                            }}
                                        >
                                            <input
                                                type="radio"
                                                checked={aktifSecim === "serbest"}
                                                onChange={() => setAktifSecim("serbest")}
                                            />
                                            <span style={{ fontWeight: 600 }}>
                                                Serbest Seçim Şubesine Ata:
                                            </span>
                                            <select
                                                value={serbestSecim}
                                                onChange={e => setSerbestSecim(e.target.value)}
                                                style={styles.select}
                                                disabled={aktifSecim !== "serbest"}
                                            >
                                                <option value="">Bir şube seçin...</option>
                                                {tumSubeler.map(s => (
                                                    <option key={s.isim} value={s.isim}>{s.isim}</option>
                                                ))}
                                            </select>
                                        </label>
                                    </div>
                                    <div style={{ display: "flex", justifyContent: "center", marginTop: 16 }}>
                                        <button
                                            onClick={() => subeDegistir(secilenSube)}
                                            style={styles.button}
                                            disabled={!secilenSube}
                                        >
                                            Şube Devrini Gerçekleştir
                                        </button>
                                    </div>
                                </div>
                            )}
                        </>
                    ) : (
                        <div style={styles.successBox}>
                            <h2 style={{ color: '#be1e2d', fontWeight: 700, marginBottom: 8 }}>Şube Değişikliği Başarılı!</h2>
                            <p>
                                <b>Eski şubeniz:</b> <span style={{ color: '#be1e2d' }}>{eskiSube}</span><br />
                                <b>Yeni şubeniz:</b> <span style={{ color: '#333' }}>{mevcutSube}</span>
                            </p>
                            <p style={{ marginTop: 16, color: '#222' }}>İyi günler dileriz.</p>
                        </div>
                    )}
                </div>

                {/* YENİ: Yükleniyor overlay */}
                {aiYukleniyor && (
                    <div style={{
                        position: "absolute",
                        inset: 0,
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        background: "rgba(255,255,255,0.68)",
                        zIndex: 99,
                        borderRadius: 20
                    }}>
                        <span style={{ fontSize: 19, color: "#be1e2d", fontWeight: 700 }}>
                            Bekleyiniz... <Spinner />
                        </span>
                    </div>
                )}

            </div>
        </div>
    );
}

