import React, { useState } from 'react';
import axios from 'axios';

const WelcomeScreen = ({ adSoyad, sube, girisBilgisi }) => {
    const [yeniSube] = useState('Beşiktaş Şubesi'); // Statik öneri
    const [aiOneri, setAiOneri] = useState(null);    // Gemini önerisi
    const [aiAciklama, setAiAciklama] = useState(""); // **YENİ: Açıklama**
    const [enYakinSube, setEnYakinSube] = useState(null); // Örn: Kadıköy
    const [mesaj, setMesaj] = useState('');
    const [guncellendi, setGuncellendi] = useState(false);

    const tercihOpsiyonlari = [
        'ATM yoğunluğu az',
        'Engelli erişimi var',
        'Yoğunluk düşük',
        'Park yeri mevcut',
        'Uzun çalışma saatleri',
        'Kolay ulaşım'
    ];
    const [secimler, setSecimler] = useState([]);

    const tercihDegistir = (tercih) => {
        if (secimler.includes(tercih)) {
            setSecimler(secimler.filter((s) => s !== tercih));
        } else {
            setSecimler([...secimler, tercih]);
        }
    };

    const subeDegistir = async () => {
        try {
            const response = await axios.post('http://localhost:5000/sube-guncelle', {
                girisBilgisi,
                yeniSube: aiOneri || yeniSube
            });

            if (response.data.basarili) {
                setGuncellendi(true);
                setMesaj(response.data.mesaj);
            } else {
                setMesaj(response.data.mesaj);
            }
        } catch (error) {
            setMesaj('Sunucu hatası oluştu.');
        }
    };

    const getAiSubeOnerisi = async () => {
        try {
            const response = await axios.post('http://localhost:5000/gemini', {
                il: 'İstanbul',
                secimler,
                konum: sube
            });

            if (response.data.oneri) {
                setAiOneri(response.data.oneri);
                setAiAciklama(response.data.aciklama || ""); // **Açıklama**
                setEnYakinSube(response.data.enYakin);
                setMesaj('');
            } else {
                setMesaj('Yapay zekâ önerisi alınamadı.');
            }
        } catch (err) {
            setMesaj('Yapay zekâ hizmetine ulaşılamadı.');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Hoş geldiniz, {adSoyad}!</h2>
            <p>Mevcut şubeniz: <strong>{sube}</strong></p>

            {!guncellendi ? (
                <>
                    <p><strong>Yapay zekâ destekli şube önerisi almak ister misiniz?</strong></p>

                    <div style={{ marginTop: '10px' }}>
                        <p><strong>Şube tercihlerinizi seçin:</strong></p>
                        {tercihOpsiyonlari.map((tercih) => (
                            <label key={tercih} style={{ display: 'block', margin: '5px 0' }}>
                                <input
                                    type="checkbox"
                                    checked={secimler.includes(tercih)}
                                    onChange={() => tercihDegistir(tercih)}
                                />{" "}
                                {tercih}
                            </label>
                        ))}
                    </div>

                    <button onClick={getAiSubeOnerisi} style={styles.aiBtn}>
                        Yapay Zekâ ile Şube Öner
                    </button>

                    {aiOneri && (
                        <>
                            <p><strong>Yapay Zekâ Önerisi:</strong> {aiOneri}</p>
                            {aiAciklama && (
                                <p style={{ fontStyle: "italic", color: "#004fa3" }}>{aiAciklama}</p>
                            )}
                            {enYakinSube && enYakinSube !== aiOneri && (
                                <p><strong>En yakın şube:</strong> {enYakinSube}</p>
                            )}
                            <button onClick={subeDegistir} style={styles.button}>
                                Bu şubeye geç
                            </button>
                        </>
                    )}

                    {mesaj && <p style={{ color: 'red' }}>{mesaj}</p>}
                </>
            ) : (
                <p style={{ color: 'green', marginTop: '20px' }}>{mesaj}</p>
            )}
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '60px',
        padding: '20px',
        maxWidth: '500px',
        margin: 'auto',
        backgroundColor: '#fff',
        border: '1px solid #ccc',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)'
    },
    button: {
        padding: '10px 20px',
        marginTop: '15px',
        backgroundColor: '#be1e2d',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    },
    aiBtn: {
        padding: '10px 20px',
        marginTop: '15px',
        backgroundColor: '#0066cc',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    }
};

export default WelcomeScreen;
