import React, { useState } from 'react';
import axios from 'axios';

const WelcomeScreen = ({ adSoyad, sube, girisBilgisi }) => {
    const [yeniSube] = useState('Beşiktaş Şubesi'); // Sabit öneri (manuel)
    const [guncellendi, setGuncellendi] = useState(false);
    const [mesaj, setMesaj] = useState('');
    const [aiOneri, setAiOneri] = useState('');
    const [enYakinSube, setEnYakinSube] = useState('');

    const subeDegistir = async () => {
        try {
            const response = await axios.post('http://localhost:5000/sube-guncelle', {
                girisBilgisi,
                yeniSube
            });

            if (response.data.basarili) {
                setGuncellendi(true);
                setMesaj(response.data.mesaj);
            } else {
                setMesaj(response.data.mesaj);
            }
        } catch (error) {
            setMesaj('Sunucu hatası oluştu.');
        }
    };

    const getAiSubeOnerisi = async () => {
        try {
            const response = await axios.post('http://localhost:5000/gemini', {
                il: 'İstanbul',
                secimler: ['ATM yoğunluğu az', 'Engelli erişimi var'], // şimdilik sabit
                konum: sube // mevcut şubeyi konum olarak alıyoruz
            });

            if (response.data) {
                setAiOneri(response.data.aiOneri || '');
                setEnYakinSube(response.data.enYakinSube || '');
            }
        } catch (error) {
            setAiOneri('Yapay zekâ önerisi alınamadı.');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Hoş geldiniz, {adSoyad}!</h2>
            <p>Mevcut şubeniz: <strong>{sube}</strong></p>

            {!guncellendi ? (
                <>
                    <p>Önerilen yeni şube: <strong>{yeniSube}</strong></p>
                    <button onClick={subeDegistir} style={styles.button}>
                        Bu şubeye geç
                    </button>

                    <button onClick={getAiSubeOnerisi} style={{ ...styles.button, backgroundColor: '#007BFF' }}>
                        Yapay Zekâ ile Şube Öner
                    </button>
                </>
            ) : (
                <p style={{ color: 'green', marginTop: '20px' }}>{mesaj}</p>
            )}

            {aiOneri && (
                <div style={{ marginTop: '20px' }}>
                    <p><strong>Yapay Zekâ Önerisi:</strong> {aiOneri}</p>
                    <p><strong>En Yakın Şube:</strong> {enYakinSube}</p>
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '80px'
    },
    button: {
        padding: '10px 20px',
        marginTop: '10px',
        marginLeft: '10px',
        backgroundColor: '#be1e2d',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    }
};

export default WelcomeScreen;
